    if (typeof msg.payload === 'number' && Number.isInteger(msg.payload)) {
        // Check if the message payload is a timestamp (assumed to be an integer)
        // If it is a timestamp, output the current value of x
        msg.payload = dnv;
        return msg;
    }

    // Check if the message payload is a boolean
    else if (typeof msg.payload === 'boolean') {
        // Convert boolean to number
        context.global.dnv = +msg.payload;
        return null;
    }

    // If the message is not a boolean message or timestamp, do nothing
    return null;
}

// Process incoming messages
msg = processMessage(msg);

// Return the modified message or null if no modification is needed
return msg;

